mapper.update:
update user_address
set is_deleted = 1,
gmt_modified = #{updated,jdbcType=BIGINT}
where id = #{addressId,jdbcType=INTEGER}

mapper.select:
select id ,name,
tel, prov_code  as provCode, city_code cityCode, dist_code as distCode, address,
is_default as isDefault, gmt_create as created, gmt_modified as updated from user_address where id=#{addressId}
