mapper.update:
  		update users

			    	user_id = #{userId}, 
			        device_id=#{deviceId},  
			        wz_id=#{wzId},
			        mobile_phone=#{phoneNum}  

			    where user_id = #{userId}

mapper.select:
  	select id from identified_code

  			phoneNum = #{phoneNum}

		  	 and code = #{code} 

		  	and DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%S') < DATE_ADD(FROM_UNIXTIME(date, '%Y-%m-%d %H:%i:%S') ,INTERVAL 1 MINUTE)
